package com.takieo;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	byte b =10;
	switch(b)
	{
	case 10:
		System.out.println("..........10........");
		break;
	case 20:
		System.out.println("..........20........");
		break;
	case 30:
		System.out.println("..........30........");
		break;
	default:
		System.out.println("Select above statement");
		
		
	}
		

	}

}
